#!/bin/sh
# set -e

# With the new build system, the iso must be generated by running the lumos-builder-build docker service.
# . ./bin/iso.sh

# Error out if the iso file does not exist
if [ ! -f theOS.iso ]; then
    echo "theOS.iso not found. Please run the builder docker service first."
    exit 1
fi

mkdir -p logs && touch logs/log.txt
qemu-system-i386 -m 4G -serial file:logs/log.txt -cdrom theOS.iso
